
class CreatingObject_Using_new {
	
	public void Harish() {
		System.out.println("Harish class is called by using object");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		CreatingObject_Using_new h = new CreatingObject_Using_new();        //h object is created
        h.Harish();               // by using object above class is called
	}

}
